parameters.inputSchema:
{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Strict QuickChart Config",
  "type": "object",
  "required": ["type", "data"],
  "properties": {
    "type": {
      "type": "string",
      "enum": ["bar", "line", "pie", "doughnut", "radar", "scatter", "bubble", "progressBar", "gauge", "radialGauge", "boxplot", "violin", "sankey", "funnel"] 
    },
    "data": {
      "type": "object",
      "required": ["labels", "datasets"],
      "properties": {
        "labels": {
          "type": "array",
          "items": { "type": ["string", "number"] }
        },
        "datasets": {
          "type": "array",
          "items": {
            "type": "object",
            "required": ["data"],
            "properties": {
              "label": { "type": "string" },
              "data": {
                "type": "array",
                "items": { "type": ["number", "object", "null"] }
              },
              "backgroundColor": {
                "oneOf": [
                  { "type": "string" },
                  { "type": "array", "items": { "type": "string" } }
                ]
              },
              "borderColor": {
                "oneOf": [
                  { "type": "string" },
                  { "type": "array", "items": { "type": "string" } }
                ]
              },
              "fill": { "type": ["boolean", "string"] }
            },
            "additionalProperties": false
          }
        }
      },
      "additionalProperties": false
    },
    "options": {
      "type": "object",
      "properties": {
        "responsive": { "type": "boolean" },
        "maintainAspectRatio": { "type": "boolean" },
        "title": {
          "type": "object",
          "properties": {
            "display": { "type": "boolean" },
            "text": { "type": "string" }
          },
          "additionalProperties": false
        },
        "scales": {
          "type": "object",
          "properties": {
            "x": { "type": "object" },
            "y": { "type": "object" },
            "xAxes": { "type": "array", "items": { "type": "object" } },
            "yAxes": { "type": "array", "items": { "type": "object" } }
          },
          "additionalProperties": false
        },
        "plugins": { "type": "object" }
      },
      "additionalProperties": false
    },
    "version": {
      "type": "string",
      "enum": ["2", "3", "4"]
    },
    "backgroundColor": { "type": "string" },
    "width": { "type": "integer", "minimum": 1 },
    "height": { "type": "integer", "minimum": 1 },
    "devicePixelRatio": { "type": "integer", "enum": [1, 2] },
    "format": { "type": "string", "enum": ["png", "webp", "jpg", "svg", "pdf", "base64"] },
    "encoding": { "type": "string", "enum": ["url", "base64"] }
  },
  "additionalProperties": false
}
